mapper.select:
SELECT IFNULL( o.door, '' ) door, IFNULL( con.contract_id, '' ) id, IFNULL( l.`name`, '' ) `name`, IFNULL( r.pay_type, '' ) payTime, IFNULL( con.price, '' ) price, IFNULL( r.start_time, '' ) `start`, IFNULL( r.end_time, '' ) `end`, IFNULL( ord.paid_in_price, '' ) paid, IFNULL( con.state, '' ) state FROM t_contract con LEFT JOIN t_lessee l ON con.lessee_id = l.id LEFT JOIN t_rule r ON r.id = con.rule_id LEFT JOIN t_office o ON o.id = con.room LEFT JOIN t_shared_office so ON so.id = o.shared_office_id LEFT JOIN t_office_building ob ON ob.id = o.office_building LEFT JOIN t_order ord ON ord.contract_id=con.contract_id WHERE 1=1
AND ( so.`name` LIKE CONCAT('%',#{condition},'%') OR ob.`name` LIKE CONCAT('%',#{condition},'%') )
AND ( o.shared_office_id = #{id} OR o.office_building = #{id} )
AND o.door=#{door}
AND DATE_FORMAT(now(), '%Y%m' ) =DATE_FORMAT(con.time,'%Y%m') ORDER BY con.time DESC;
