mapper.select:
SELECT b.*, IFNULL(mp.position,'') position, IFNULL(bd.`name`,'') `business_district_name`, IFNULL(mp.latitude,'') mp_latitude, IFNULL(mp.longitude,'') mp_longitude, IFNULL(b.picture,'') photo FROM t_office_building b LEFT JOIN t_metro_to_office_building mtb ON b.id = mtb.office_building_id LEFT JOIN t_metro_position mp ON mp.id = mtb.metro_position_id LEFT JOIN t_district_to_office_building dtob ON dtob.office_building_id=b.id LEFT JOIN t_business_district bd ON bd.business_district_id=dtob.business_district_id LEFT JOIN t_collect c ON c.collect_office_building_id=b.id LEFT JOIN a_user u ON u.user_id=c.collect_user WHERE 1=1 AND c.collect_user= #{myId}
AND (b.`name` LIKE CONCAT('%',#{condition},'%') or b.address LIKE CONCAT('%',#{condition},'%'))
AND b.area BETWEEN #{areaLow} AND #{areaHigh}
AND b.price BETWEEN #{priceLow} AND #{priceHigh}
AND b.fitment = #{fitment}
AND bd.`name`= #{name}
AND mp.position= #{position}
AND b.address LIKE CONCAT('%',#{address},'%')
order by b.price ASC
order by b.price desc

SELECT b.*, IFNULL( mp.position, '' ) position, IFNULL( bd.`name`, '' ) `business_district_name`, IFNULL( mp.latitude, '' ) mp_latitude, IFNULL( mp.longitude, '' ) mp_longitude, IFNULL( b.picture, '' ) photo FROM t_office_building b LEFT JOIN t_metro_to_office_building mtb ON b.id = mtb.office_building_id LEFT JOIN t_metro_position mp ON mp.id = mtb.metro_position_id LEFT JOIN t_district_to_office_building dtob ON dtob.office_building_id = b.id LEFT JOIN t_business_district bd ON bd.business_district_id = dtob.business_district_id LEFT JOIN t_attention t ON t.attention_label = b.id LEFT JOIN a_user u ON u.user_id = t.attention_user WHERE 1 = 1 AND t.attention_user = #{userId} AND t.attention_type='A'
AND (b.`name` LIKE CONCAT('%',#{condition},'%') or b.address LIKE CONCAT('%',#{condition},'%'))
AND b.area BETWEEN #{areaLow} AND #{areaHigh}
AND b.price BETWEEN #{priceLow} AND #{priceHigh}
AND b.fitment = #{fitment}
AND bd.`name`= #{name}
AND mp.position= #{position}
AND b.address LIKE CONCAT('%',#{address},'%')
order by b.price ASC
order by b.price desc

SELECT b.*, IFNULL( mp.position, '' ) position, IFNULL( bd.`name`, '' ) `business_district_name`, IFNULL( mp.latitude, '' ) mp_latitude, IFNULL( mp.longitude, '' ) mp_longitude, IFNULL( b.picture, '' ) photo FROM t_office_building b LEFT JOIN t_metro_to_office_building mtb ON b.id = mtb.office_building_id LEFT JOIN t_metro_position mp ON mp.id = mtb.metro_position_id LEFT JOIN t_district_to_office_building dtob ON dtob.office_building_id = b.id LEFT JOIN t_business_district bd ON bd.business_district_id = dtob.business_district_id WHERE 1 = 1
AND (b.`name` LIKE CONCAT('%',#{condition},'%') or b.address LIKE CONCAT('%',#{condition},'%'))
AND b.area BETWEEN #{areaLow} AND #{areaHigh}
AND b.price BETWEEN #{priceLow} AND #{priceHigh}
AND b.fitment = #{fitment}
AND bd.`name`= #{name}
AND mp.position= #{position}
AND b.address LIKE CONCAT('%',#{address},'%')
order by b.price ASC
order by b.price desc
