SELECT o.*, IFNULL(mp.position,'') position, IFNULL(bd.`name`,'') `business_district_name`, IFNULL(mp.latitude,'') mp_latitude, IFNULL(mp.longitude,'') mp_longitude, IFNULL(o.picture,'') photo FROM t_office o LEFT JOIN t_metro_to_office mto ON o.id = mto.office_id LEFT JOIN t_metro_position mp ON mp.id = mto.metro_id LEFT JOIN t_district_to_office dto ON dto.office_id=o.id LEFT JOIN t_business_district bd ON bd.business_district_id=dto.district_id WHERE 1=1
AND (o.`name` LIKE CONCAT('%',#{condition},'%') or o.address LIKE CONCAT('%',#{condition},'%'))
AND ( o.shared_office_id = #{id} OR o.office_building = #{id} )
AND o.area BETWEEN #{areaLow} AND #{areaHigh}
AND o.price BETWEEN #{priceLow} AND #{priceHigh}
AND o.fitment = #{fitment}
AND bd.`name`= #{name}
AND mp.position= #{position}
and o.address LIKE CONCAT('%',#{address},'%')
order by o.price ASC
order by o.price desc

SELECT o.*, IFNULL(mp.position,'') position, IFNULL(bd.`name`,'') `business_district_name`, IFNULL(mp.latitude,'') mp_latitude, IFNULL(mp.longitude,'') mp_longitude, IFNULL(o.picture,'') photo FROM t_office o LEFT JOIN t_metro_to_office mto ON o.id = mto.office_id LEFT JOIN t_metro_position mp ON mp.id = mto.metro_id LEFT JOIN t_district_to_office dto ON dto.office_id=o.id LEFT JOIN t_business_district bd ON bd.business_district_id=dto.district_id LEFT JOIN t_attention t ON t.attention_label = o.id LEFT JOIN a_user u ON u.user_id = t.attention_user WHERE 1=1 AND t.attention_user = #{userId} AND t.attention_type='C'
AND (o.`name` LIKE CONCAT('%',#{condition},'%') or o.address LIKE CONCAT('%',#{condition},'%'))
AND o.area BETWEEN #{areaLow} AND #{areaHigh}
AND o.price BETWEEN #{priceLow} AND #{priceHigh}
AND ( o.shared_office_id = #{id} OR o.office_building = #{id} )
AND o.fitment = #{fitment}
AND bd.`name`= #{name}
AND mp.position= #{position}
and o.address LIKE CONCAT('%',#{address},'%')
order by o.price ASC
order by o.price desc

SELECT o.*, IFNULL(mp.position,'') position, IFNULL(bd.`name`,'') `business_district_name`, IFNULL(mp.latitude,'') mp_latitude, IFNULL(mp.longitude,'') mp_longitude, IFNULL(o.picture,'') photo FROM t_office o LEFT JOIN t_metro_to_office mto ON o.id = mto.office_id LEFT JOIN t_metro_position mp ON mp.id = mto.metro_id LEFT JOIN t_district_to_office dto ON dto.office_id=o.id LEFT JOIN t_business_district bd ON bd.business_district_id=dto.district_id LEFT JOIN t_collect_office co ON co.collect_office_id = o.id LEFT JOIN a_user u ON u.user_id = co.collect_user WHERE 1=1 AND co.collect_user= #{myId}
AND (o.`name` LIKE CONCAT('%',#{condition},'%') or o.address LIKE CONCAT('%',#{condition},'%'))
AND o.area BETWEEN #{areaLow} AND #{areaHigh}
AND ( o.shared_office_id = #{id} OR o.office_building = #{id} )
AND o.price BETWEEN #{priceLow} AND #{priceHigh}
AND o.fitment = #{fitment}
AND bd.`name`= #{name}
AND mp.position= #{position}
and o.address LIKE CONCAT('%',#{address},'%')
order by o.price ASC
order by o.price desc

SELECT IFNULL(o.area,'')area, IFNULL(o.door,'')door, IFNULL(f.`name`,'')`name`, IFNULL(o.price,'')price, IFNULL(o.commission,'')commission, IFNULL(o.id,'')id FROM t_office o LEFT JOIN t_floor_to_office fto ON o.id = fto.office_id LEFT JOIN t_floor f ON f.id = fto.floor_id LEFT JOIN t_attention a ON a.attention_label =o.id WHERE 1=1 AND a.attention_type='C' AND a.attention_user=#{id}
and ( o.office_building = #{pid} OR o.shared_office_id = #{pid})
AND o.door =#{door}

SELECT IFNULL( o.id, '' ) id, IFNULL( o.`name`, '' ) `name`, IFNULL( o.door, '' ) door FROM t_office o LEFT JOIN t_attention a ON a.attention_label = o.id WHERE 1=1
AND o.office_building = #{id}
AND o.shared_office_id = #{id}
AND a.attention_user =#{uid}
